import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View, Button, Alert} from 'react-native';

function Separator() {
  return <View style={styles.separator} />;
}

export default function App() {

  const [cnt, setCnt] = useState(0);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Number Counter</Text>
      <Text style={styles.textTitle}>Current Number</Text>
      <Text style={styles.cnt}>{cnt}</Text>
      <Button title='더하기' onPress={ () => setCnt(cnt + 1) }/>
      <Button title='더하기' onPress={ () => setCnt(cnt - 1) }/>
      <Button title='Check' onPress={ () => Alert.alert('현재 숫자는 ' + cnt + ' 입니다.') }/>

      <Separator/>

      <StatusBar style="auto" />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: Constants.statusBarHeight,
    marginHorizontal: 16
  },
  title:{
    textAlign: 'center',
    paddingVertical: 20,
    fontSize: 35
  },
  textTitle:{
    textAlign: 'center',
    backgroundColor: 'red',
    marginVertical: 8,
    fontSize: 20
  },
  cnt:{
    fontSize: 40,
  },
  separator:{
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth
  },
  buttonSize: {
    margin: 10
  }
});
